mapper.select:
select *
from orders


        and order_time > #{order.beginTime}


        and order_time < #{order.endTime}


        and number like concat('%',#{order.number},'%')


        and status = #{order.status}


        and phone like concat('%',#{order.phone},'%')


order by order_time desc

select count(id) as allOrders, count(case when status = 6 then 1 end) as cancelledOrders, count(case when status = 5 then 1 end) as completedOrders, count(case when status = 3 then 1 end) as deliveredOrders, count(case when status = 2 then 1 end) as waitingOrders from orders
and order_time > #{beginTime}
and order_time < #{endTime}
